function validator(){var e="Error:\n";var h=document.signin.team_name;var j=document.signin.password;var a=document.signin.confirm_password;var b=document.signin.email;var d=document.signin.phone;var g=document.signin.manager_name;var c=0;if(h.value.length==0){++c;h.style.backgroundColor="Yellow";e+=c.toString()+". Enter Team Name.\n"}else{if(h.value.length>25){++c;h.style.backgroundColor="Yellow";e+=c.toString()+". Team Name is too long. Maximum 25 Character.\n"}}if(j.value.length==0){++c;j.style.backgroundColor="Yellow";e+=c.toString()+". Enter Password.\n"}if(j.value.length<8&&j.value.length!=0){++c;e+=c.toString()+". Password too short. Minimum 8 characters.\n"}if(a.value.length==0){++c;a.style.backgroundColor="Yellow";e+=c.toString()+". Enter Confirm Password.\n"}if(b.value.length==0){++c;b.style.backgroundColor="Yellow";e+=c.toString()+". Enter Email 1.\n"}if(d.value.length==0){++c;d.style.backgroundColor="Yellow";e+=c.toString()+". Please enter a mobile number.\n"}if(g.value.length==0){++c;g.style.backgroundColor="Yellow";e+=c.toString()+". Enter Manager name.\n"}if(j.value!=a.value){++c;j.style.backgroundColor="Yellow";a.style.backgroundColor="Yellow";e+=c.toString()+". Password and confirm password do not match.\n"}for(var f=0;f<d.value.length;f++){if(isNaN(d.value[f])){++c;d.style.backgroundColor="Yellow";e+=c.toString()+". Enter a valid mobile number.\n";break}}if(e!="Error:\n"){confirm(e);return false}else{document.getElementById("reg").action="/register";document.getElementById("reg").method="post";document.getElementById("reg").submit()}};